		for (int j = max(y - 1, 0); j < min(y + 2, H); j++) {
			if (st[i][j] == '.')
				c++;
		}
	}
	return c >= conv(st[x][y]);

}

inline bool collapse(int x, int y) {
	if (is_castle(st[x][y]) && state(x,y)) {
		sts[x][y] = '.';
		return true;
	}
	return false;
}

bool wave() {
	bool e = false;
	copy(sts, st);
	for (int i = 0; i < H; i++) {
		for (int j = 0; j < W; j++) {
			e = collapse(i, j) ? true : e;
		}
	}
	copy(st, sts);
	return e;
}

