int houkaisuru(int y, int x) {
	int count = 0;
	int i, j;
	if (map[y][x] <= 0) return 0;
	for (i = -1; i <= 1; i++) {
		for (j = -1; j <= 1; j++) {
			if (i == 0 && j == 0) continue;
			if (map[y + i][x + j] == 0) count++;
		}
	}
	return map[y][x] <= count;
}

