	int i, j, k;
	int count = 0;
	int (*current_target)[2] = target0, ct_num = 0;
	int (*next_target)[2] = target1, nt_num = 0;
	char (*current_mark)[1024] = mark0;
	char (*next_mark)[1024] = mark1;
	int (*target_temp)[2];
	char (*mark_temp)[1024];
	int houkai_num = 0;
