	for (i = 0; i < H; i++) {
		if (scanf("%1023s", map[i]) != 1) return 1;
		for (j = 0; j < W; j++) {
			if (isdigit((unsigned char)map[i][j])) {
				map[i][j] -= '0';
			} else {
				map[i][j] = 0;
			}
		}
	}
	for (i = 1; i < H - 1; i++) {
		for (j = 1; j < W - 1; j++) {
			current_target[ct_num][0] = i;
