				houkai_num++;
				for (j = -1; j <= 1; j++) {
					for (k = -1; k <= 1; k++) {
						if (j == 0 && k == 0) continue;
						if (next_mark[y + j][x + k] == 0) {
							next_mark[y + j][x + k] = 1;
							next_target[nt_num][0] = y + j;
							next_target[nt_num][1] = x + k;
							nt_num++;
						}
					}
				}
			}
		}
		if (houkai_num == 0) break;
		count++;
		for (i = 0; i < houkai_num; i++) {
			map[houkai[i][0]][houkai[i][1]] = 0;
		}
