#include <stdio.h>
int map[50][50];
int H, W;
int sum = 0;

void d(int sh, int sw, int h, int w)
{
  int i, j, k, f = -1;
  int mh[49], mw[49];
  for (i = sh; i < h; i++){
    for (j = sw; j < w; j++){
      if (map[i][j] != 0){
        k = 0;
        k += (map[i + 1][j - 1] == 0) ? 1 : 0;
        k += (map[i + 1][j] == 0) ? 1 : 0;
        k += (map[i + 1][j + 1] == 0) ? 1 : 0;
        k += (map[i][j - 1]) == 0 ? 1 : 0;
        k += (map[i][j + 1] == 0) ? 1 : 0;
        k += (map[i - 1][j - 1] == 0) ? 1 : 0;
        k += (map[i - 1][j] == 0) ? 1 : 0;
        k += (map[i - 1][j + 1] == 0) ? 1 : 0;
        if (map[i][j] <= k){
          f++;
          mh[f] = i;
          mw[f] = j;
        }
      }
    }
  }
  for (k = 0; k < f + 1; k++){
    map[mh[k]][mw[k]] = 0;
  }
  for (k = 0; k < f + 1; k++){
    for (i = -1; i <= 1; i++){
      for (j = -1; j <= 1; j++){
        if (map[mh[k] + i][mw[k] + j] != 0){
          d(mh[k] + i, mw[k] + j, mh[k] + i + 1, mw[k] + j + 1);
        }
      }
    }
    if (f >= 0) sum++;
  }
}

int main(void)
{
  int i, j;
  char c;
  scanf("%d %d ", &H, &W);
  for (i = 0; i < H; i++){
    for (j = 0; j < W; j++){
      if (j == W - 1) scanf("%c%*c", &c);
      else scanf("%c", &c);
      if (c == '.') map[i][j] = 0;
      else map[i][j] = c - 0x30;
    }
  }
  d(1, 1, H - 1, W - 1);
  printf("%d", sum);
  return (0);
}

