int dy[8]={-1,0,1,1,1,0,-1,-1};

int chk(int x,int y){
    if(x<h && y<w && x>=0 && y>=0){
        return cmap[x][y];
    }
    return -1;
}

int check(int x,int y){
    int i,c=0;
    for(i=0;i<8;i++){
        if(chk(x+dx[i],y+dy[i])>0) c++;
    }
    if(c<map[x][y]){
        return 0;
    }
    map[x][y]=0;
    return 1;
}

