import java.util.*


data class Coordinate(val x:Int, val y:Int){
    inline fun forEachNeighbors(func:(Int, Int) -> Unit):Unit {
        (-1 .. 1).forEach { dx ->
            if (dx == 0) (-1 .. 1 step 2).forEach { func(x + dx, y + it) }
            else (-1..1).forEach { func(x + dx, y + it) }
        }
    }
}
fun main(args:Array<String>):Unit {
    val (h, w) = readLine()!!.trim().split(' ').map(String::toInt)
    val state = Array(h){readLine()!!.trim().map{if (it == '.') 0 else it.toString().toInt()}.toIntArray()}
    val queue :Deque<Coordinate> = LinkedList<Coordinate>()
    state.forEachIndexed { y, chars -> chars.forEachIndexed { x, c -> if (c == 0) queue.push(Coordinate(x, y)) } }
    var count = 0
    if (queue.size == h * w) {
        println(0)
        return
    }
    while (queue.isNotEmpty()){
        ++count
        repeat(queue.size){
            queue.removeFirst().forEachNeighbors { x, y ->
                if (x in 0 until w && y in 0 until h && state[y][x] > 0){
                    --state[y][x]
                    if (state[y][x] == 0) queue.addLast(Coordinate(x, y))
                }
            }
        }
    }
    println(count - 1)
}

