private static int bfs(int h,int w,int[][] map){
Deque<Pair> que=new ArrayDeque<Pair>();
int[][] hmap=new int[h][];
int ans=-1;
for(int i=0;i<h;i++) hmap[i]=map[i].clone();
for(int i=0;i<h;i++){
for(int j=0;j<w;j++){
if(map[i][j]!=INF){
que.offerLast(new TPair(0,i,j));
}
}
}
while(!que.isEmpty()){
TPair p=que.pollFirst();
if(ans!=p.t){
for(int i=0;i<h;i++) hmap[i]=map[i].clone();
ans=p.t;
}
int x=p.x;
int y=p.y;
int nx;
int ny;
int cnt1=0;
int cnt2=0;
int hxy=hmap[x][y];
loop:
for(int i=0;i<3;i++){
for(int j=0;j<3;j++){
if(i==1&&j==1) continue;
nx=x+d[i];
ny=y+d[j];
if(hmap[nx][ny]==INF) cnt1++;
if(hmap[nx][ny]<INF) cnt2++;
if(cnt1>=hxy){
map[x][y]=INF;
break loop;
}
if(cnt2>8-hxy){
que,offerLast(new TPair(ans+1,x,y));
break loop;
}
}
}
return ans;
}
}

