					er.add(n);
					for(int i = 0; i < 8; i++) {
						int tx = n.w + vx[i];
						int ty = n.h + vy[i];
						if(r(h,w,ty,tx) && map[ty][tx] != 0 && !cc[ty][tx]) {
							n_q.add(new Node(ty,tx));
							cc[ty][tx] = true;
						}
					}
				} else {
					n_q.add(n);
				}

			}
			while(!er.isEmpty()) {
				Node p = er.poll();
				map[p.h][p.w] = 0;
			}
			ArrayDeque<Node> tmp = q;
			q = n_q;
			n_q = tmp;
			if(o) ans++;
		}
		System.out.println(ans);

	}

	static boolean r(int a, int b, int c, int d) {
		if(c < 0 || d < 0 || c >= a || d >= b) return false;
		return true;
	}

	static class Node {
		int h;
		int w;
		Node(int a, int b) {
			h = a;
			w = b;
		}
	}
}

