import java.util.*;
import java.math.*;
import java.awt.geom.*;
import java.io.*;


public class Main {
	static int INF = 2 << 29;
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int h = sc.nextInt();
		int w = sc.nextInt();
		char[][] map = new char[h][w];
		for(int i = 0; i < h; i++) {
			map[i] = sc.next().toCharArray();
		}
		boolean[][] al = new boolean[h][w];
		ArrayDeque<Data> queue = new ArrayDeque<Data>();

		for(int i = 0; i < h; i++) {
			for(int j = 0; j < w; j++) {
				if(al[i][j]) continue;
				if(map[i][j] == '.') {
					al[i][j] = true;
					for(int k = 0; k < 8; k++) {
						int ty = i + vy[k];
						int tx = j + vx[k];
						if(ty < 0 || tx < 0 || ty >= h || tx >= w) continue;
						if(al[ty][tx]) continue;
						al[ty][tx] = true;
						if(map[ty][tx] != '.') {
							queue.add(new Data(ty,tx));
						}
					}
				}
			}
		}
		int cou = 0;
		while(!queue.isEmpty()) {
			cou++;
			boolean ok = false;
			ArrayDeque<Data> tmp = new ArrayDeque<Data>();
			ArrayDeque<Data> er  = new ArrayDeque<Data>();
			while(!queue.isEmpty()) {
				Data p = queue.poll();
				int count = 0;
				for(int i = 0; i < 8; i++) {
					int ty = p.y + vy[i];
					int tx = p.x + vx[i];
					if(ty < 0 || tx < 0 || ty >= h || tx >= w) continue;
					if(map[ty][tx] == '.') count++;
				}
				if(count >= map[p.y][p.x] - '0') {
					ok = true;
					er.add(p);
					for(int i = 0; i < 8; i++) {
						int ty = p.y + vy[i];
						int tx = p.x + vx[i];
						if(ty < 0 || tx < 0 || ty >= h || tx >= w) continue;
						if(map[ty][tx] != '.' && !al[ty][tx]) {
							al[ty][tx] = true;
							tmp.add(new Data(ty,tx));
						}
					}
				}
				else {
					tmp.add(p);
				}
			}
			while(!er.isEmpty()) {
				Data p = er.poll();
				map[p.y][p.x] = '.';
			}
			if(!ok) {
				break;
			}
			queue.addAll(tmp);
			tmp.clear();
		}

		System.out.println(cou-1);




	}
	static int[] vx = {-1,0,1,-1,0,1,-1,0,1};
	static int[] vy = {-1,-1,-1,0,0,0,1,1,1};

	static class Data {
		int y;
		int x;
		Data(int a, int b) {
			y = a;
			x = b;
		}
	}

}

import java.util.*;

class Main {
    static int W;
    static int H;
    static int s[][];
    public static void main(String args[]) {
        Scanner sc = new Scanner(System.in);
        H = sc.nextInt();
        W = sc.nextInt();
        s = new int[H][W];
        for (int i = 0; i < H; i++) {
            String line = sc.next();
            for (int j = 0; j < W; j++) {
                char c = line.charAt(j);
                if (c == '.') {
                    s[i][j] = 0;
                } else {
                    s[i][j] = c - '0';
                }
            }
        }
        int count = 0;
        Set<Integer> set = rush0();
        while (set.size() > 0) {
            for(int x: set) {
                int i = x >> 16;
                int j = x & 0xffff;
                s[i][j] = 0;
            }
            count++;
            set = rush1(set);
        }
        System.out.printf("%d\n", count);
    }

    static Set<Integer> rush1(Set<Integer> set) {
        Set<Integer> set2 = new HashSet<Integer>();
        for(int x: set) {
            int p = x >> 16;
            int q = x & 0xffff;
            for (int i = p-1; i <= p+1; i++) {
                for (int j = q-1; j <= q+1; j++) {
                    if (i == p && j == q) continue;
                    if (s[i][j] > 0 && count(i, j) >= s[i][j]) {
                        set2.add(i << 16 | j);
                    }
                }
            }
        }
        return set2;
    }

    static int count(int p, int q) {
        int c = 0;
        for (int i = p-1; i <= p+1; i++) {
            for (int j = q-1; j <= q+1; j++) {
                if (s[i][j] == 0) c++;
            }
        }
        return c;
    }

    static Set<Integer> rush0() {
        Set<Integer> set = new HashSet<Integer>();
        for (int i = 1; i < H-1; i++) {
            for (int j = 1; j < W-1; j++) {
                if (s[i][j] > 0 && count(i, j) >= s[i][j]) {
                    set.add(i << 16 | j);
                }
            }
        }
        return set;
    }
}

