                    var ny = p.y + dy[i];
                    if (nx >= 0 && nx < h && ny >= 0 && ny < w && map[nx, ny] != 0 && map[nx, ny] != 9)
                    {
                        sc[nx, ny]++;
                        if (sc[nx, ny] >= map[nx, ny])
                        {
                            var wa = new Po { x = nx, y = ny };
                            if (!q2.Contains(wa)) q2.Enqueue(wa);
                        }
                    }
                }
            }
        }
        else
        {
            for (int i = 0; i < q2.Count(); i++)
            {
                var p = q2.Dequeue(); q2.Enqueue(p);
                map[p.x, p.y] = 0;
