public class Hello
{
    static int h, w;
    static int[] dx, dy;
    static int[,] map;
    public static void Main()
    {
        dx = new int[] { 0, 1, 1, 1, 0, -1, -1, -1 };
        dy = new int[] { 1, 1, 0, -1, -1, -1, 0, 1 };
        string[] line = Console.ReadLine().Trim().Split(' ');
        h = int.Parse(line[0]);
        w = int.Parse(line[1]);
        map = new int[h, w];
        for (int i = 0; i < h; i++)
        {
            var s = Console.ReadLine().Trim();
            for (int j = 0; j < w; j++)
                if (s[j] != '.') map[i, j] = int.Parse(s[j].ToString());
        }
        var ans = getAns();
        Console.WriteLine(ans);
    }
    static int getAns()
    {
        var a = getA();
        if (a.Count() == 0) return 0;
        var b = new List<int>();
        var count = 1;
        changeSand(a);
        while (true)
        {
            AtoB(a, b);
            if (b.Count() == 0) return count;
            b = b.Distinct().ToList();
            changeSand(b);
            count++;
            AtoB(b, a);
            if (a.Count() == 0) return count;
            a = a.Distinct().ToList();
            changeSand(a);
            count++;
        }
    }
    static void AtoB(List<int> a, List<int> b)
    {
        b.Clear();
        foreach (var x in a)
        {
            for (int i = 0; i < 8; i++)
            {
                var nx = x / w + dx[i];
                var ny = x % w + dy[i];
                var nmp = map[nx, ny];
                if (nmp >= 1 && nmp <= 8)
                {
                    var count = 0;
                    for (int j = 0; j < 8; j++)
                    {
                        var nnx = nx + dx[j];
                        var nny = ny + dy[j];
                        if (nnx >= 0 && nnx < h && nny >= 0 && nny < w &&
                            map[nnx, nny] == 0)
                        {
                            count++;
                            if (count >= nmp) { b.Add(nx * w + ny); break; }
                        }
                    }
                }
            }
        }
    }
    static void changeSand(List<int> a)
    {
        foreach (var x in a)
            map[x / w, x % w] = 0;
    }
    static List<int> getA()
    {
        var a = new List<int>();
        for (int i = 0; i < h; i++)
            for (int j = 0; j < w; j++)
            {
                var mp = map[i, j];
                if (mp >= 1 && mp <= 8)
                {
                    var count = 0;
                    for (int k = 0; k < 8; k++)
                    {
                        var nx = i + dx[k];
                        var ny = j + dy[k];
                        if (map[nx, ny] == 0)
                        {
                            count++;
                            if (count >= mp) { a.Add(i * w + j); break; }
                        }
                    }
                }
            }
        return a;
    }
}

