import java.util.Scanner;

public class Mizuyokan {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int N = sc.nextInt();  // number of segments
        int[] lengths = new int[N];  // lengths of each segment
        for (int i = 0; i < N; i++) {
            lengths[i] = sc.nextInt();
        }
        int left = 0, right = 0;
        for (int length : lengths) {
            right += length;
        }
        int result = right;

        while (left <= right) {
            int mid = (left + right) / 2;
            if (isPossible(lengths, N, mid)) {
                result = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }

        System.out.println(result);
    }

    private static boolean isPossible(int[] lengths, int N, int maxPieceSize) {
        // Attempt to divide the Mizuyokan while maintaining all piece sizes â‰¤ maxPieceSize
        int pieceSum = 0;
        int minPieceSize = 0;
        for (int i = 0; i < N; i++) {
            if (lengths[i] > maxPieceSize) {
                return false;  // single piece is larger than maxPieceSize
            }
            pieceSum += lengths[i];
            if (pieceSum > maxPieceSize) {
                if (minPieceSize < pieceSum - lengths[i]) {
                    minPieceSize = pieceSum - lengths[i];
                }
                pieceSum = lengths[i];
            }
        }
        return maxPieceSize - minPieceSize >= 0;
    }
}

