int minimizeDifference(int N, int lengths[]) {
    int totalLength = 0;
    for (int i = 0; i < N; ++i) {
        totalLength += lengths[i];
    }

    int prefixSum[N+1];
    prefixSum[0] = 0;
    for (int i = 1; i <= N; ++i) {
        prefixSum[i] = prefixSum[i-1] + lengths[i-1];
    }

    int answer = INT_MAX;
    for (int k = 1; k <= N; ++k) {
        for (int i = 0; i + k <= N; ++i) {
            int minPiece = totalLength, maxPiece = 0;
            for (int j = i; j <= i + k; ++j) {
                int pieceLength = prefixSum[j+1] - prefixSum[i];
                if (pieceLength < minPiece) minPiece = pieceLength;
                if (pieceLength > maxPiece) maxPiece = pieceLength;
            }
            int diff = maxPiece - minPiece;
            if (diff < answer) {
                answer = diff;
            }
        }
    }
    return answer;
}

