// Function to check if we can have the max-min difference <= d
bool isFeasible(vector<int>& lengths, int cuts, int d) {
    int n = lengths.size();
    int minLen = INT_MAX;
    int maxLen = 0;
    int count = 1, sum = 0;

    for (int i = 0; i < n; i++) {
        sum += lengths[i];
        minLen = min(minLen, sum);
        maxLen = max(maxLen, sum);

        if (maxLen - minLen > d) {
            count++;
            minLen = INT_MAX;
            maxLen = 0;
            sum = lengths[i];
        }
    }

    return count <= cuts;
}

