string conv(int p,int t) {
    if(p==s.length()) return "";
    int c=0;
    while(p<s.length() && s[p]==t+'0')c++,p++;
    return string((t==0)?(c-1):1,b[t][(c-1)%(b[t].length())]) + conv(p,s[p]-'0');
}

