                    if( (count - 1) % 6 < 3 ) c = 'j' + (count - 1) % 3;
                    else c = 'J' + (count - 1) % 3;
                }else if(s[i] == '6'){
                    if( (count - 1) % 6 < 3 ) c = 'm' + (count - 1) % 3;
                    else c = 'M' + (count - 1) % 3;
                }else if(s[i] == '7'){
                    if( (count - 1) % 8 < 4 ) c = 'p' + (count - 1) % 3;
                    else c = 'P' + (count - 1) % 4;
                }else if(s[i] == '8'){
