                    else c = 'T' + (count - 1) % 3;
                }else if(s[i] == '9'){
                    if( (count - 1) % 8 < 4 ) c = 'w' + (count - 1) % 3;
                    else c = 'W' + (count - 1) % 4;
                }
                if(s[i] == '0') rep(i,count - 1) cout << ' ';
