		{
			if(str[i-1] == str[i])
			{
				if(str[i]=='0')
				{
					ans += " ";
				}
				else
				{
					n++;
				}
			}
			else
			{
				if(str[i-1]!='0')
				{
					ans += bt[str[i-1]-'0'][n%bt[str[i-1]-'0'].length()];
				}
				n=0;
			}
		}

		cout << ans << endl;
	}
}

