char button[10][10] = {
    " ",        "',.!?",    "abcABC",   "defDEF",
    "ghiGHI",   "jklJKL",   "mnoMNO",
    "pqrsPQRS", "tuvTUV",   "wxyzWXYZ"
};

queue<int> splitString(string digit);
string translate(string digit, queue<int> splits);

int main(){
    queue<string> mails;
    queue<int> splits;
    string digits;
    string sentence;

    while (cin>>digits) {
        splits = splitString(digits);
        sentence = translate(digits, splits);
        mails.push(sentence);
    }

    while (!mails.empty()) {
        cout<<mails.front()<<endl;
        mails.pop();
    }

    return 0;
}

queue<int> splitString(string digit){
    queue<int> splits;
    int length = digit.length();

    for(int i = 0; i < length - 1; i++){
        if(digit[i] != digit[i + 1]){
            if(digit[i+1] == '0'){
                splits.push(i+2);
                i++;
            }
            else{
                splits.push(i+1);
            }
        }
    }

    splits.push(length);
    return splits;
}

string translate(string digit, queue<int> splits){
    int length = digit.length();
    int count;
    queue<char> chars;

    for(int i = 0; i < length; ){
        int k = splits.front();
        if(digit[i] == '0'){
            for(int j = i; j < k; j++){
                chars.push(' ');
            }
        }
        else if (digit[k-1] == '0'){
            count = k - 1 - i;
            chars.push(button[digit[i] - '0'][count - 1]);
        }
        else{
            count = k - i;
            chars.push(button[digit[i] - '0'][count - 1]);
        }

        i = k;
        splits.pop();
    }

    length = chars.size();
    char *sentence = new char[length + 1];
    sentence[length] = '\0';
    for(int i = 0; i < length; i++){
        sentence[i] = chars.front();
        chars.pop();
    }

    string str = sentence;
    return str;
}

