    for(int i=0;i<in.size();i++){
      if(f){
        num = in[i];
        cnt++;
        f = false;
      }else{
        if(in[i] == '0'){
          if(num == '0'){
            cnt++;
          }else{
            out += conv(num, cnt);
            num = in[i];
            cnt = 0;
          }
        }else if(num != in[i]){
          if(num == '0') while(cnt--) out += ' ';
          else out += conv(num, cnt);
          num = in[i];
          cnt = 1;
        }else{
          cnt++;
        }
      }
    }
    if(num == '0') while(cnt--) out += ' ';
    else out += conv(num, cnt);
    cout << out << endl;
  }
}

