		}else{
			if( prev_button == 0 ){
				prev_button = s[i] - '0';
				cnt = 0;
				ch = B[ s[i] - '0' ][0];
			}else if( prev_button == (s[i] - '0') ){
				int k = B[ s[i] - '0' ].size();
				prev_button = s[i] - '0';
				cnt = (cnt + 1) % k;
				ch = B[ s[i] - '0' ][cnt];
			}else{
				prev_button = s[i] - '0';
				cnt = 0;
				ans.push_back( ch );
				ch = B[ s[i] - '0' ][0];
			}
		}
	}
	return ans;
}

