				res += s[prev - 1][cnt % s[prev - 1].size()];
				prev = str[i] - '0';
				cnt = 0;
			}
			else if (prev == str[i] - '0'){
				cnt++;
			}

		}
		if (prev) res += s[prev - 1][cnt % s[prev - 1].size()];
		cout << res << endl;
	}

	return 0;
}

