void translate(char c,int n)
{
	int i;
	switch(c)
	{
	case '0':
		for(i=0;i<n-1;i++)cout<<" ";break;
	case '1':cout<<Num_1[n%5];break;
	case '2':cout<<Num_2[n%6];break;
	case '3':cout<<Num_3[n%6];break;
	case '4':cout<<Num_4[n%6];break;
	case '5':cout<<Num_5[n%6];break;
	case '6':cout<<Num_6[n%6];break;
	case '7':cout<<Num_7[n%8];break;
	case '8':cout<<Num_8[n%6];break;
	case '9':cout<<Num_9[n%8];break;

	}
}
void convert(char str[])
{
	int i=0;
	int cnt=0;
	while(str[i]!='\0')
	{
		if(str[i] != str[i+1])
		{
			cnt++;
			translate(str[i],cnt);
			cnt=0;
		}
		else cnt++;
		i++;
	}
}
int main()
{
	char str[10010];
	while(cin>>str)
	{
		convert(str);
		cout<<endl;
	}
	return 0;
}

