    string ret = "";
    if (s[0] == '0') {
        for (int i=1; i<(int)s.size(); ++i) {
            ret += " ";
        }
    } else {
        int k = (s.size() - 1) % btn[s[0]].size();
        ret = btn[s[0]][k];
