string conv(string &s,int p,int st) {
    if(p == s.length()) return "";
    int cnt = 0;
    while(p < s.length() && s[p] == st + '0') cnt++,p++;

    return string((st==0)?(cnt-1):1,but[st][(cnt-1)%(but[st].length())])
        + conv(s,p,s[p]-'0');
}

