#include <iostream>
#include <string>
using namespace std;

int charToInt(char c) {
  return (int)c - (int)'0';
}

int main() {
  string str;
  string button[10] = {" ", "',.!?", "abcABC", "defDEF", "ghiGHI", "jklJKL", "mnoMNO", "pqrsPQRS", "tuvTUV", "wxyzWXYZ"};
  while(getline(cin, str)) {
    char bfr;
    int cnt = 0;
    for(int i = 0; i < str.length(); i++) {
      int j;
      for(j = i+1; j < str.length(); j++) {
	if(str[j] != str[i]) {
	  break;
	}
      }
      int cnt = j-i;
      i = j-1;

      if(str[i] == '0') {
	cnt--;
	for(int k = 0; k < cnt; k++) {
	  cout << ' ';
	}
      } else {
	cout << button[charToInt(str[i])][cnt-1];
      }
    }
    cout << endl;
  }
  return 0;
}

