string t[10] = {" ", "',.!?", "abcABC", "defDEF", "ghiGHI", "jklJKL", "mnoMNO", "pqrsPQRS", "tuvTUV", "wxyzWXYZ"};

int main(){
    string s;
    while(cin >> s){
        int n = stoi(s.substr(0, 1)), count = 1;
        string ret;
        for(int i=0; i< s.size()-1; i++){
            if(n == stoi(s.substr(i+1, 1)))count++;
            else {
                if(n == 0) for(int i=0; i < count-1; i++) ret += " ";
                else ret += t[n][count-1];
                n = stoi(s.substr(i+1, 1)); count = 1;
            }
        }
        ret += t[n][count-1];
        cout << ret << endl;
