vector<pair<char, int> > RLE(const string& S){
  vector<pair<char, int> > res;
  rep(i, S.size()){
    int count = 0;
    reps(j, i, S.size()){
      if (S[i] != S[j]) break;
      count++;
    }
    res.push_back(make_pair(S[i], count));
    i += count - 1;
  }
  return res;
}

