string conv(string &s,int p,int st) {
    if(p == s.length()) return "";
    int cnt = 0;
    while(p < s.length() && s[p] == st + '0') {
        cnt++;p++;
    }

    if(st == 0) return string(cnt-1,' ') + conv(s,p,s[p]-'0');
    return string(1,but[st][(cnt-1)%(but[st].length())])
        + conv(s,p,s[p]-'0');
}

