#define END ' '
int main()
{
  string list[10]={
    " ",
    "',.!?",
    "abcABC",
    "defDEF",
    "ghiGHI",
    "jklJKL",
    "mnoMNO",
    "pqrsPQRS",
    "tuvTUV",
    "wxyzWXYZ",
  };
  string s,ans;
  int i,cnt;
  while(cin>>s){
    s+=END;
    ans="";
    cnt=0;
    for(i=1;i<s.size();i++){
      if(s[i-1]==s[i]){
	if(s[i-1]=='0')ans+=' ';
	cnt++;
      }else{
	if(s[i-1]!='0'){
	  int t=s[i-1]-'0';
	  ans+=list[t][cnt%list[t].size()];
	}
	cnt=0;
      }
    }
    cout<<ans<<endl;
  }
}

