    for(int j='A'+i*3;j<'A'+i*3+3;j++) mp[i+2].push_back(j);
  }
  for(int i='p';i<='s';i++) mp[7].push_back(i);
  for(int i='P';i<='S';i++) mp[7].push_back(i);
  for(int i='t';i<='v';i++) mp[8].push_back(i);
  for(int i='T';i<='V';i++) mp[8].push_back(i);
  for(int i='w';i<='z';i++) mp[9].push_back(i);
