}

string solve(string str){
  if(str[str.size()-1]!='0')str+='0';  
  string res;
  for(int i=0;i<str.size();i++){
    int cnt=0;
    if(str[i]=='0'){res+=' ';continue;}
    while(str[i]==str[i+1])i++,cnt++;
    res+=mp[str[i]-'0'][cnt%mp[str[i]-'0'].size()];    
    if(str[i+1]=='0')i++;
  }
  return res;
}

