char buff[40010];
int u[10010];
int s[10010];
int n;

int memo[2][10010];

int solve(int now, int us){
  if(now == n){
    if(s[now]){
      if(us == 0) return 3;
      else return 2;
    }else if(u[now]){
      return 1;
    }else{
      return us;
    }
  }

  if(memo[us][now]) return memo[us][now];

  int ret = INT_MAX;

  if(u[now] && s[now]){
    ret = min(ret, 2 + solve(now+1, 0));
    ret = min(ret, 2 + solve(now+1, 1));
  }else if(u[now]){
    ret = min(ret, 1 + solve(now+1, 1));
    ret = min(ret, 1 + solve(now+1, 0));
  }else if(s[now]){
    if(us == 0){
      ret = 2 + solve(now+1, 1);
    }else{
      ret = 1 + solve(now+1, 1);
    }
  }else{
    ret = solve(now+1, us);
  }

  //printf("solve(%d,%d)=%d\n",now,us,ret+1);
  return memo[us][now] = ret + 1;
}

