      s[i+1] = ((buff[n+n+2*i+1] == 'Y') | (buff[n+n+2*(i+1)] == 'Y'));
    }

    //REP(i,n+1) printf("%d ",u[i]); puts("");
    //REP(i,n+1) printf("%d ",s[i]); puts("");

    memset(memo,0,sizeof(memo));
