				if(zeroflag) ans += v[0];
				else {
					zeroflag = true;
					ans += v[a][b];
					//cout<<" a = "<<a<<" b = "<<b<<" "<<ans<<endl;
					a = b = 0;
				}
			}
			else{
				if(zeroflag) a = s[i] - '0';
				else{
					if(s[i] == (char)('0' + a)){
						b = ( b + 1) % v[a].size();
					}
					else{
						ans += v[a][b];
						a = s[i] - '0';
						b = 0;
					}
				}
				zeroflag = false;
			}
		}
		if((a == 0 && zeroflag) || a > 0) ans += v[a][b];
		cout<<ans<<endl;
	}
}

