				return_word.append(1, input[index][(cell_input.length()-last_pos-1) % len_max]);
			}
			return return_word;
		}
		if(temp_word.compare("0") == 0){
			return_word.append(temp_pos-last_pos-1, ' ');
		}else{
			return_word.append(1, input[index][(temp_pos-last_pos-1) % len_max]);
		}
		last_pos = temp_pos;
	}		
}

