			result_str = strs[(n-1) % strs.length];
			break;
		}
		case 4:{
			String strs[] = {"g", "h", "i", "G", "H", "I"};
			result_str = strs[(n-1) % strs.length];
			break;
		}
		case 5:{
			String strs[] = {"j", "k", "l", "J", "K", "L"};
			result_str = strs[(n-1) % strs.length];
			break;
