        {
            var ans = getAns(s);
            Console.WriteLine(ans);
        }
    }
    static string getAns(string s)
    {
        var ans = "";
        var sL = s.Length;
        var pre = s[0] - '0';
        var count = 1;
        var p = 1;
        while (p < sL)
        {
            var t = s[p] - '0';
            if (t == pre) { count++; }
            else
            {
                if (pre == 0 && count > 1) ans += new string(' ', count - 1);
                else if (pre != 0) ans += a[pre][count - 1];
                count = 1;
            }
            pre = t;
            p++;
        }
        ans += a[pre][count - 1];
        return ans;
    }
}

