using namespace std;

int h[20][20];
int dx[]{ 1,-1 }, dy[]{ -1,1 };
int main() {
	int n;
	int t = 0;
	while (scanf("%d", &n), n) {
		h[0][0] = 1;
		int x = 0, y = 1;
		int idx = 0, p = 1;
		while (p < n*n) {
			int px = x, py = y;
			while (0 <= px&&px < n && 0 <= py&&py < n) {
				h[px][py] = ++p;
				px += dx[idx]; py += dy[idx];
			}
			idx = !idx;
			if (x == 0 || y == 0) {
				if (x == n-1 && y == 0) {
					swap(x, y);
					x++;
				}
				else if (x == 0 && y == n-1) {
					swap(x, y);
					y++;
				}
				else {
					swap(x, y);
					if (x == 0)y++;
					else x++;
				}
			}
			else {
				swap(x, y);
				if (x < y)x++; else y++;
			}
		}
		printf("Case %d:\n", ++t);
		rep(i, n) {
			rep(j, n) {
				printf("%3d", h[i][j]);
			}
			printf("\n");
		}
	}
}

