int n;

int solve(){

  int i=0,j=0;
  int d=1;
  bool left=false;

  while(d<=n*n){

    mp[i][j]=d;

    if(left){
      if(n<=i+1){
	left=false;
	j++;
      }
      else if(j-1<0){
	left=false;
	i++;
      }
      else{
	j--; i++;
      }
    }
    else{
      if(n<=j+1){
	left=true;
	i++;
      }
      else if(i-1<0){
	left=true;
	j++;
      }
      else{
	j++; i--;
      }
    }
    d++;
  }

}



