                di = -1;
                dj = 1;
                ++j;
            } else if (j == 0 && dj == -1) {
                di = -1;
                dj = 1;
                ++i;
            } else if (i == 0 && di == -1) {
                di = 1;
                dj = -1;
                ++j;
            } else {
                i += di;
                j += dj;
            }
        }
        REP(i, N) {
