typedef vector <int> VI;
typedef vector <VI> VVI;

int main( void )
{
	int n, datacases = 1;
	while ( cin >> n && n )
	{
		VVI image( n, VI( n, 0 ) );

		int x = 0, y = 0, time = 1;
		bool phase = false;

		while ( x != n-1 || y != n-1 )
		{
			image[x][y] = time;
			if ( phase )
			{
				if ( x < n-1 && y == 0 )
				{
					x++;
					phase = !phase;
				}
				else if ( x == n - 1 )
				{
					y++;
					phase = !phase;
				}
				else
				{
					x++;
					y--;
				}
			}
			else
			{
				if ( y < n-1 && x == 0 )
				{
					y++;
					phase = !phase;
				}
				else if ( y == n - 1 )
				{
					x++;
					phase = !phase;
				}
				else
				{
					x--;
					y++;
				}
			}
			time++;
		}
		image[n-1][n-1] = time;

		cout << "Case " << datacases << ":" << endl;
		for ( int i = 0; i < n; i++ )
		{
			for ( int j = 0; j < n; j++ )
			{
				cout << setw(3) << image[i][j];
			}
			cout << endl;
		}

		datacases ++;
	}
	return 0;
}

