struct ZigZag {
  const int N;
  int data[16][16];
  int index = 1;
  ZigZag(int N) : N(N), index(1) {}
  void build(){
    int x = 0, y = 0;
    for (int i = 0; i < N - 1; i++){
      zig(x, y);
      if (x + 1 < N) x++;
      else y++;
      zag(x, y);
      if (x - 1 < 0 && y + 1 < N) y++;
      else x++;
    }
    zig(x, y);
  }
  void zig(int& x, int& y){
    while (0 <= x && x < N && 0 <= y && y < N){
      data[y--][x++] = index++;
    }
    y++, x--;
  }
  void zag(int& x, int& y){
    while (0 <= x && x < N && 0 <= y && y < N){
      data[y++][x--] = index++;
    }
    y--, x++;
  }
};

int main()
{
  int case_no = 1;
  while (int N = in()){
    ZigZag z(N);
    z.build();
    printf("Case %d:\n", case_no++);
    for (int y = 0; y < N; y++){
      for (int x = 0; x < N; x++){
        printf("%3d", z.data[y][x]);
      }
      puts("");
    }
  }
  return 0;
}

