	vx = 1;
	vy = 0;
	flag = true;
      }

      x += vx;
      y += vy;
      if(x < 0 || x >= n) {
	x -= vx;
	y -= vy;
	vx = 0;
	vy = 1;
	continue;
      } else if(y < 0 || y >= n) {
	x -= vx;
	y -= vy;
	vx = 1;
	vy = 0;
	continue;
      }

      if((!flag && vy == 0) || (flag && vx == 0)) {
	vx = -1;
	vy = 1;
      } else if((!flag && vx == 0) || (flag && vy == 0)) {
	vx = 1;
	vy = -1;
      }
      jpeg[y][x] = num++;
    }

    cout << "Case " << cnt++ << ':' << endl;
