#define SORT(v) sort((v).begin(), (v).end())
#define REVERSE(v) reverse((v).begin(), (v).end())
typedef complex<double> P;
const int MAX_N = 10;

int v[MAX_N][MAX_N];

int my[] = {0, 1, 1, -1}, mx[] = {1, -1, 0, 1}, N, c = 1;

void solve(int y, int x, int d, int num, int pd, int pl, int len, int cnt){
  if(y >= N || x >= N || y < 0 || x < 0) return ;
  v[y][x] = num;
  if(N % 2 == 0 && y + 1 == N && x == 0){ pd = -1; len -= 2; pl = -1; d = 0; }
  if(N % 2 == 1 && y == 0 && x + 1 == N){ pd = -1; len -= 2; pl = -1; d = 2; }
  if(d == 0 || d == 2) solve(y + my[d], x + mx[d], (pd == 1 ? (d + pd) % 4 : (d + pd < 0 ? 3 : d + pd)), num + 1, pd, pl, len, 0);
  else solve(y + my[d], x + mx[d], (cnt >= len ? (pd == 1 ? (d + pd) % 4 : (d + pd < 0 ? 3 : d + pd)) : d), num + 1, pd, pl, (cnt >= len ? len + pl : len), (cnt >= len ? 0 : cnt + 1));
}

int main() {
  while(cin >>N && N){
    cout <<"Case " <<c++ <<":" <<endl;
    memset(v, 0, sizeof(v));
    solve(0, 0, 0, 1, 1, 1, 0, 0);
    REP(i, N){
      REP(j, N) printf("%3d", v[i][j]);
      cout <<endl;
    }
  }
