void saiki(int x,int y,int d,int cnt){
  int dx[]={0,1,-1,1},dy[]={1,-1,1,0};

  if(used[y][x][d])return;
  used[y][x][d]=1;
  mp[y][x]=min(mp[y][x],cnt);

  int nx=x+dx[d],ny=y+dy[d];
  if(d==0){ 
    if(ny<n)saiki(nx,ny,x==0?1:2,cnt+1); 
    else saiki(x,y,3,cnt);
  }
  if(d==1){ 
    if(nx==n) saiki(x,y,0,cnt);
    else if(ny<0)saiki(x,y,3,cnt);
    else  saiki(nx,ny,1,cnt+1);
  }
  if(d==2){
    if(ny>=n)saiki(x,y,3,cnt);
    else if(nx<0) saiki(x,y,0,cnt);
    else saiki(nx,ny,2,cnt+1);
  }
  if(d==3){
    if(nx>=n) saiki(x,y,0,cnt);
    else saiki(nx,ny,ny==0? 2:1,cnt+1);
  }
}

