			while(j!=m*m && x-1>=0 && y+1<m && mat[x-1][y+1]==0){
				--x;++y;++j;
				mat[x][y]=j;
			}
		}
		cout<<"Case "<<++n<<":"<<endl;
		for(int x=0;x<m;++x){
			for(int y=0;y<m;++y){
				cout<<just(mat[x][y]);
			}
			cout<<endl;
		}
	}
	return 0;
}

