int a[101][101];
int dy[]={1,-1};
int dx[]={-1,1};
void get(int n){
  if(n==1)return ;
  int y=0,x=0,d=0;
  int c=2;
  int f=1;
  while(1){
    if(y==n-1&&x==n-1)break;
    if(f&&y==0){
      if(x==n-1)a[++y][x]=c++;
      else a[y][++x]=c++;
      d=0;f=0;
    }
    else if(f&&x==0){
      if(y==n-1)a[y][++x]=c++;
      else a[++y][x]=c++;
      d=1;f=0;
    }
    else if(f&&x==n-1){
      a[++y][x]=c++;
      d=0;f=0;
    }
    else if(f&&y==n-1){
      a[y][++x]=c++;
      d=1;f=0;
    }
    else{
      f=1;
      y+=dy[d];
      x+=dx[d];
      a[y][x]=c++;
    }
  }
}
int main(){
  int p=1,n;
  a[0][0]=1;
  while(cin>>n,n){
    printf("Case %d:\n",p++);
    get(n);
    r(i,n){
